-- Part 1
select count(1)
from day04 X
    inner join day04 M on M.char = 'M' and abs(X.y-M.y) <= 1 and abs(X.x-M.x) <= 1
    inner join day04 A on A.char = 'A' and M.y-A.y = X.y-M.y and M.x-A.x = X.x-M.x
    inner join day04 S on S.char = 'S' and A.y-S.y = X.y-M.y and A.x-S.x = X.x-M.x
where X.char = 'X';

-- Part 2
select count(1)
from (
    select a.x, a.y
    from day04 A
        inner join day04 M on M.char = 'M' and abs(A.y-M.y) = 1 and abs(A.x-M.x) = 1
        inner join day04 S on S.char = 'S' and abs(A.y-S.y) = 1 and abs(A.x-S.x) = 1
    where A.char = 'A' and A.y-M.y != A.y-S.y and A.x-M.x != A.x-S.x
    group by a.x, a.y
    having count(1) = 2) sub;

